import { createContext, useContext, useState } from 'react';

// Create a Context for authentication
export const AuthContext = createContext(null);

// Auth Provider component to wrap the application
export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null); // 'user' can be null or a user object

  const login = (userData) => {
    // In a real app, this would involve API calls, token storage etc.
    setUser(userData || { name: 'Guest' }); // Mock user data
    console.log('User logged in:', userData || { name: 'Guest' });
  };

  const logout = () => {
    // In a real app, clear tokens, user session etc.
    setUser(null);
    console.log('User logged out.');
  };

  // The value provided to consumers of this context
  const authContextValue = {
    user,
    isAuthenticated: !!user,
    login,
    logout
  };

  return (
    <AuthContext.Provider value={authContextValue}>
      {children}
    </AuthContext.Provider>
  );
};

// Custom hook to easily consume auth context
export const useAuth = () => useContext(AuthContext);

import React from 'react';
import { Route, Redirect } from 'react-router-dom';
import { useAuth } from './AuthContext'; // Assuming AuthContext is in AuthContext.js

// A custom PrivateRoute component
function PrivateRoute({ children, ...rest }) {
  const { isAuthenticated } = useAuth();

  return (
    <Route
      {...rest}
      render={({ location }) =>
        isAuthenticated ? (
          children
        ) : (
          <Redirect
            to={{
              pathname: '/login',
              state: { from: location } // Pass current location to redirect after login
            }}
          />
        )
      }
    />
  );
}

export default PrivateRoute;

