import { createContext, useContext, useState, useEffect } from 'react';

export const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true); // Optional: for initial auth check

  // Simulate an initial check for a logged-in user, e.g., from localStorage
  useEffect(() => {
    const storedUser = localStorage.getItem('currentUser');
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
    setLoading(false);
  }, []);

  const login = (userData) => {
    // In a real app, send credentials to backend, get token, set user
    const newUser = { id: '1', name: userData.username || 'Test User' };
    setUser(newUser);
    localStorage.setItem('currentUser', JSON.stringify(newUser)); // Simulate persisting login
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem('currentUser'); // Simulate clearing session
  };

  const authContextValue = {
    user,
    isAuthenticated: !!user,
    login,
    logout,
    loading // Useful for waiting on initial auth check
  };

  return (
    <AuthContext.Provider value={authContextValue}>
      {!loading ? children : <div>Loading authentication...</div>}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
