import { BrowserRouter as Router, Route, Switch, Link, useParams } from 'react-router-dom';

const HomePage = () => {
  return (
    <div>
      <h2>Welcome!</h2>
      <p>Explore dynamic user profiles:</p>
      <ul>
        <li><Link to="/users/123">Go to User 123 Profile</Link></li>
        <li><Link to="/users/alice">Go to Alice's Profile</Link></li>
        <li><Link to="/users/bob-456">Go to Bob's Profile</Link></li>
      </ul>
      <p><Link to="/">Back to Home</Link></p>
    </div>
  );
};

function UserProfile() {
  const { userId } = useParams(); // Access userId using useParams

  return (
    <div>
      <h2>User Profile</h2>
      <p>Currently viewing profile for:</p>
      <h1>User ID: {userId}</h1>
      <p><Link to="/">Back to Home</Link></p>
    </div>
  );
}

function AppRouter() {
  return (
    <Router>
      <Switch>
        <Route exact path="/">
          <HomePage />
        </Route>
        {/* Define the route for /users/:userId */}
        <Route path="/users/:userId">
          <UserProfile />
        </Route>
      </Switch>
    </Router>
  );
}

export default AppRouter;

