import React from 'react';
import { BrowserRouter as Router, Route, Switch, Link, NavLink, useParams, useRouteMatch } from 'react-router-dom';

const HomePage = () => {
  return (
    <div>
      <h2>Welcome!</h2>
      <p>Explore dynamic user profiles with nested sections:</p>
      <ul>
        <li><Link to="/users/123">Go to User 123 Profile</Link></li>
        <li><Link to="/users/alice">Go to Alice's Profile</Link></li>
      </ul>
    </div>
  );
};

const UserOverview = () => {
  const { userId } = useParams(); // Can access params directly in nested components
  return <h3>User {userId} Overview</h3>;
};

const PostsPage = () => {
  const { userId } = useParams();
  return <h3>User {userId} Posts</h3>;
};

const PhotosPage = () => {
  const { userId } = useParams();
  return <h3>User {userId} Photos</h3>;
};

function UserProfile() {
  const { userId } = useParams();
  const { path, url } = useRouteMatch(); // For v5+, useRouteMatch provides path and url

  return (
    <div style={{ padding: '20px', border: '1px solid #ddd', borderRadius: '8px', margin: '20px' }}>
      <h2>User Profile for ID: {userId}</h2>
      <nav>
        <ul style={{ listStyleType: 'none', padding: 0 }}>
          <li style={{ display: 'inline', marginRight: '15px' }}>
            <NavLink exact to={url} activeClassName="sub-active-link">Overview</NavLink>
          </li>
          <li style={{ display: 'inline', marginRight: '15px' }}>
            <NavLink to={`${url}/posts`} activeClassName="sub-active-link">Posts</NavLink>
          </li>
          <li style={{ display: 'inline', marginRight: '15px' }}>
            <NavLink to={`${url}/photos`} activeClassName="sub-active-link">Photos</NavLink>
          </li>
        </ul>
      </nav>

      <div style={{ marginTop: '20px', borderTop: '1px solid #eee', paddingTop: '15px' }}>
        <Switch>
          {/* Default nested route for /users/:userId */}
          <Route exact path={path}>
            <UserOverview />
          </Route>
          <Route path={`${path}/posts`}>
            <PostsPage />
          </Route>
          <Route path={`${path}/photos`}>
            <PhotosPage />
          </Route>
        </Switch>
      </div>
      <p style={{ marginTop: '20px' }}><Link to="/">Back to Home</Link></p>
    </div>
  );
}

function AppRouter() {
  return (
    <Router>
      <Switch>
        <Route exact path="/">
          <HomePage />
        </Route>
        {/* Parent route for user profiles */}
        <Route path="/users/:userId">
          <UserProfile />
        </Route>
        {/* Add a fallback 404 route (optional) */}
        <Route path="*">
          <div>
            <h2>404 - Page Not Found</h2>
            <p>The page you are looking for does not exist.</p>
            <Link to="/">Go to Home</Link>
          </div>
        </Route>
      </Switch>
    </Router>
  );
}

export default AppRouter;

