import React, { createContext, useContext, useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch, Redirect, useHistory, useLocation } from 'react-router-dom';

// --- Auth Context Setup ---
export const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const storedUser = localStorage.getItem('currentUser');
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
    setLoading(false);
  }, []);

  const login = (userData) => {
    const newUser = { id: '1', name: userData.username || 'Test User' };
    setUser(newUser);
    localStorage.setItem('currentUser', JSON.stringify(newUser));
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem('currentUser');
  };

  const authContextValue = {
    user,
    isAuthenticated: !!user,
    login,
    logout,
    loading
  };

  return (
    <AuthContext.Provider value={authContextValue}>
      {!loading ? children : <div>Loading authentication...</div>}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);

// --- Custom PrivateRoute Component ---
function PrivateRoute({ children, ...rest }) {
  const { isAuthenticated, loading } = useAuth();

  if (loading) {
    return <div>Loading...</div>; // Or a spinner
  }

  return (
    <Route
      {...rest}
      render={({ location }) =>
        isAuthenticated ? (
          children
        ) : (
          <Redirect
            to={{
              pathname: '/login',
              state: { from: location }
            }}
          />
        )
      }
    />
  );
}

// --- Page Components ---
const HomePage = () => <h2>Welcome to the Public Home Page!</h2>;

const DashboardPage = () => {
  const { logout, user } = useAuth();
  const history = useHistory();

  const handleLogout = () => {
    logout();
    history.push('/login'); // Redirect to login page after logout
  };

  return (
    <div>
      <h2>Welcome to your Private Dashboard, {user ? user.name : 'Guest'}!</h2>
      <p>You are logged in and can see this private content.</p>
      <button onClick={handleLogout}>Logout</button>
      <p><Link to="/">Go to Home Page</Link></p>
    </div>
  );
};

const LoginPage = () => {
  const history = useHistory();
  const location = useLocation();
  const { login, isAuthenticated } = useAuth();

  const { from } = location.state || { from: { pathname: '/dashboard' } };

  useEffect(() => {
    // If already authenticated and tried to access login, redirect to 'from' or dashboard
    if (isAuthenticated) {
      history.replace(from);
    }
  }, [isAuthenticated, from, history]);

  const handleLogin = () => {
    login({ username: 'user123' }); // Mock login
    // The useEffect above will handle the redirect after state updates
  };

  return (
    <div>
      <h2>Login</h2>
      <p>Enter credentials to access the dashboard.</p>
      <button onClick={handleLogin}>Log In (Mock)</button>
      {location.state && location.state.from &&
        <p style={{color: 'orange', marginTop: '10px'}}>You were redirected from: {location.state.from.pathname}. Please log in.</p>
      }
       <p style={{ marginTop: '10px' }}><Link to="/">Go to Home Page</Link></p>
    </div>
  );
};

// --- Main App Router ---
function App() {
  return (
    <Router>
      <nav style={{ marginBottom: '20px' }}>
        <ul style={{ listStyleType: 'none', padding: 0 }}>
          <li style={{ display: 'inline', marginRight: '10px' }}><Link to="/">Home</Link></li>
          <li style={{ display: 'inline', marginRight: '10px' }}><Link to="/dashboard">Dashboard (Private)</Link></li>
          <li style={{ display: 'inline', marginRight: '10px' }}><Link to="/login">Login</Link></li>
        </ul>
      </nav>
      <Switch>
        <Route exact path="/" component={HomePage} />
        <Route path="/login" component={LoginPage} />
        {/* Use PrivateRoute for protected paths */}
        <PrivateRoute path="/dashboard">
          <DashboardPage />
        </PrivateRoute>
        {/* Fallback for 404 */}
        <Route path="*">
          <div><h2>404 Not Found</h2><p>Go <Link to="/">Home</Link></p></div>
        </Route>
      </Switch>
    </Router>
  );
}

export default App;

