import React, { useContext } from 'react';
import { Navigate, useLocation } from 'react-router-dom';

// Assume AuthContext and useAuth are defined elsewhere, e.g., in an AuthProvider file
const AuthContext = React.createContext(null);
const useAuth = () => useContext(AuthContext);

// Private Route component for React Router v6
function RequireAuth({ children }) {
  const { user } = useAuth(); // Get user from authentication context
  const location = useLocation(); // Get current location

  if (!user) {
    // If no user is logged in, redirect to login page
    // Store the current location in state so we can redirect back after login
    // `replace` ensures the login page replaces the current entry in history
    return <Navigate to="/login" state={{ from: location }} replace />;
  }

  // If user is authenticated, render the children (the protected component)
  return children;
}

