import React, { useContext } from 'react';
import { Navigate, useLocation } from 'react-router-dom';

// Dummy AuthContext and useAuth for demonstration
const AuthContext = React.createContext(null);
const useAuth = () => useContext(AuthContext);

// For testing roles, you might set a mock user in AuthProvider:
// const user = { name: 'Admin User', role: 'admin' };
// const user = { name: 'Regular User', role: 'user' };
// const user = null; // No user

interface RequireRoleProps {
  children: React.ReactNode;
  allowedRoles: string[];
}

function RequireRole({ children, allowedRoles }: RequireRoleProps) {
  const { user } = useAuth();
  const location = useLocation();

  // First, check if user is authenticated at all
  if (!user) {
    // If not authenticated, redirect to login (and store location for post-login redirect)
    return <Navigate to="/login" state={{ from: location }} replace />;
  }

  // Then, check if the authenticated user has any of the allowed roles
  const userHasAllowedRole = allowedRoles.includes(user.role);

  if (!userHasAllowedRole) {
    // If authenticated but unauthorized by role, redirect to unauthorized page
    // No need to store 'from' location here, as they won't be redirected back after 'auth'
    return <Navigate to="/unauthorized" replace />;
  }

  // If authenticated and authorized, render children
  return children;
}

