import React, { useEffect, useState, createContext, useContext } from 'react';
import { Routes, Route, Link, useNavigate, useLocation } from 'react-router-dom';

// --- Auth Context and Hook ---
const AuthContext = createContext(null);

const AuthProvider = ({ children }) => {
  const [isAuthenticated, setIsAuthenticated] = useState(() => {
    return sessionStorage.getItem('isAuthenticated') === 'true';
  });

  const login = () => {
    setIsAuthenticated(true);
    sessionStorage.setItem('isAuthenticated', 'true');
  };

  const logout = () => {
    setIsAuthenticated(false);
    sessionStorage.removeItem('isAuthenticated');
  };

  const authValue = { isAuthenticated, login, logout };

  return (
    <AuthContext.Provider value={authValue}>
      {children}
    </AuthContext.Provider>
  );
};

const useAuth = () => {
  const context = useContext(AuthContext);
  if (context === null) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

// --- Public Components ---
function PublicHome() {
  const { isAuthenticated, logout } = useAuth();
  return (
    <div>
      <h2>Welcome to the Public Home Page!</h2>
      {isAuthenticated ? (
        <p>You are logged in. Go to <Link to="/dashboard">Dashboard</Link> | <button onClick={logout}>Logout</button></p>
      ) : (
        <p>Please <Link to="/login">Login</Link> to access protected content.</p>
      )}
    </div>
  );
}

// --- PrivateRoute component ---
function PrivateRoute({ children }) {
  const { isAuthenticated } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();
  const [checkedAuth, setCheckedAuth] = useState(false); // To manage loading state

  useEffect(() => {
    // Simulate an async auth check on mount if not already checked
    if (isAuthenticated === null) { // If auth state is not yet determined
        // In a real app, this would be an API call
        setTimeout(() => {
            // Assume auth check completes and updates isAuthenticated
            setCheckedAuth(true);
        }, 100); // Small delay to simulate async check
    } else {
        setCheckedAuth(true);
    }

    if (checkedAuth && !isAuthenticated) {
      console.log(`Redirecting to /login from ${location.pathname}`);
      navigate('/login', { state: { from: location } });
    }
  }, [isAuthenticated, navigate, location, checkedAuth]);

  if (!checkedAuth || !isAuthenticated) {
    return <div>Loading authentication...</div>; // Show loading while checking or if not authenticated (before redirect)
  }

  return children;
}

// --- Login component ---
function Login() {
  const { login } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();

  const from = location.state?.from?.pathname || '/dashboard';

  const handleLogin = () => {
    // Simulate API call for login
    console.log('Simulating login...');
    setTimeout(() => {
      login(); // Set isAuthenticated to true
      console.log(`Login successful. Navigating to: ${from}`);
      navigate(from, { replace: true }); // Redirect to original path or dashboard
    }, 500);
  };

  return (
    <div>
      <h2>Login Page</h2>
      <p>Please log in to access the protected content.</p>
      <button onClick={handleLogin}>Log In</button>
      <p style={{ fontSize: '0.8em', color: '#666' }}>Will redirect to: {from}</p>
    </div>
  );
}

// --- Dashboard component ---
function Dashboard() {
  const { logout } = useAuth();
  return (
    <div>
      <h1>Dashboard Content</h1>
      <p>Welcome, authenticated user!</p>
      <button onClick={logout}>Logout</button>
      <p style={{ fontSize: '0.8em', color: '#666' }}>Try refreshing the page to see protected route persistence.</p>
    </div>
  );
}

function App() {
  return (
    <AuthProvider>
      <Routes>
        <Route path="/" element={<PublicHome />} />
        <Route path="/login" element={<Login />} />
        {/* Protected Route */}
        <Route 
          path="/dashboard" 
          element={
            <PrivateRoute>
              <Dashboard />
            </PrivateRoute>
          } 
        />
        <Route path="*" element={<h2>404 Not Found</h2>} />
      </Routes>
    </AuthProvider>
  );
}

