import { createStore, applyMiddleware, compose } from 'redux';
import { routerMiddleware } from 'react-router-redux';
import rootReducer from './rootReducer'; // Assuming you have a rootReducer defined

export default function configureStore(initialState: any, history: any) {
  const routerMW = routerMiddleware(history);

  const middleWares = [
    routerMW,
    // Add other middleware here, e.g., redux-thunk or redux-saga
  ];

  const enhancers = [
    applyMiddleware(...middleWares)
  ];

  // Conditionally add Redux DevTools Extension if available and not in production
  const composeEnhancers =
    process.env.NODE_ENV !== 'production' &&
    typeof window === 'object' &&
    (window as any).__REDUX_DEVTOOLS_EXTENSION_COMPOSE__ ?
    (window as any).__REDUX_DEVTOOLS_EXTENSION_COMPOSE__ : compose;

  const store = createStore(
    rootReducer,
    initialState,
    composeEnhancers(...enhancers),
  );

  return store;
}

