import React from 'react';
import { connect } from 'react-redux';
import { push } from 'react-router-redux';

interface RootState {
  router: {
    location: {
      pathname: string;
      query: { [key: string]: string | string[] | undefined };
    };
    match: {
      params: { [key: string]: string | undefined };
    };
  };
}

interface NavigationDashboardProps {
  currentPath: string;
  queryParams: { [key: string]: string | string[] | undefined };
  pathParams: { [key: string]: string | undefined };
  navigateTo: (path: string) => void;
}

class NavigationDashboard extends React.Component<NavigationDashboardProps> {
  handleNavigateClick = () => {
    this.props.navigateTo('/settings');
  };

  render() {
    const { currentPath, queryParams, pathParams } = this.props;

    return (
      <div>
        <h2>Navigation Dashboard</h2>
        <p>Current Path: <strong>{currentPath}</strong></p>
        <p>Query Params: <code>{JSON.stringify(queryParams)}</code></p>
        <p>Path Params: <code>{JSON.stringify(pathParams)}</code></p>
        <button onClick={this.handleNavigateClick}>Go to Settings</button>
      </div>
    );
  }
}

const mapStateToProps = (state: RootState) => ({
  currentPath: state.router.location.pathname,
  queryParams: state.router.location.query,
  pathParams: state.router.match.params,
});

const mapDispatchToProps = (dispatch: any) => ({
  navigateTo: (path: string) => dispatch(push(path))
});

export default connect(mapStateToProps, mapDispatchToProps)(NavigationDashboard);

