import React from 'react';
import { RouteComponentProps } from 'react-router-dom';

interface UserProfileParams {
  id: string;
}

interface UserProfileProps extends RouteComponentProps<UserProfileParams> {}

interface UserProfileState {
  loading: boolean;
  userId: string | null;
}

class UserProfile extends React.Component<UserProfileProps, UserProfileState> {
  state: UserProfileState = {
    loading: false,
    userId: null,
  };

  componentDidMount() {
    const userId = this.props.match.params.id;
    if (userId) {
      this.fetchUserData(userId);
    }
  }

  componentDidUpdate(prevProps: UserProfileProps) {
    const currentUserId = this.props.match.params.id;
    const prevUserId = prevProps.match.params.id;

    // Re-fetch only if the ID has actually changed
    if (currentUserId !== prevUserId) {
      if (currentUserId) {
        this.fetchUserData(currentUserId);
      }
    }
  }

  fetchUserData = (id: string) => {
    this.setState({ loading: true, userId: null });
    setTimeout(() => {
      // Simulate API call success
      this.setState({ loading: false, userId: id });
      console.log(`User data fetched for ID: ${id}`);
    }, 1000);
  };

  render() {
    const { loading, userId } = this.state;
    const currentParamId = this.props.match.params.id;

    return (
      <div>
        <h2>User Profile</h2>
        <p>Current URL Param ID: <strong>{currentParamId}</strong></p>
        {loading ? (
          <p>Loading user data...</p>
        ) : userId ? (
          <p>Displaying data for User ID: {userId}</p>
        ) : (
          <p>No user data loaded.</p>
        )}
      </div>
    );
  }
}

export default UserProfile;
