tasks.solutionCode:
class ShoppingCart {
  constructor() {
    this.items = []; // Each item: { product: { id, name, price }, quantity: number }
  }

  addItem(product, quantity) {
    if (!product || typeof quantity !== 'number' || quantity <= 0) {
      console.warn('Invalid product or quantity provided.');
      return;
    }

    const existingItem = this.items.find(item => item.product.id === product.id);

    if (existingItem) {
      existingItem.quantity += quantity;
    } else {
      this.items.push({ product, quantity });
    }
  }

  removeItem(productId) {
    const initialLength = this.items.length;
    this.items = this.items.filter(item => item.product.id !== productId);
    if (this.items.length === initialLength) {
      console.log(`Product with ID ${productId} not found in cart.`);
    }
  }

  getTotalItems() {
    return this.items.reduce((total, item) => total + item.quantity, 0);
  }

  getTotalPrice() {
    return this.items.reduce((total, item) => total + (item.product.price * item.quantity), 0);
  }
}

