tasks.solutionCode:
class Notification {
  constructor(message, timestamp = new Date()) {
    this.message = message;
    this.timestamp = timestamp;
  }

  display() {
    const formattedTime = this.timestamp.toISOString().replace(/T/, ' ').replace(/\..+/, '');
    console.log(`[${formattedTime}] Message: ${this.message}`);
  }

  getMessage() {
    return this.message;
  }
}

class UrgentNotification extends Notification {
  constructor(message, priority = 5, timestamp) {
    super(message, timestamp);
    this.priority = priority;
  }

  display() {
    const formattedTime = this.timestamp.toISOString().replace(/T/, ' ').replace(/\..+/, '');
    console.log(`[URGENT] [Priority: ${this.priority}] [${formattedTime}] Message: ${this.message}`);
  }
}

class ErrorNotification extends Notification {
  constructor(message, errorCode = 'UNKNOWN', timestamp) {
    super(message, timestamp);
    this.errorCode = errorCode;
  }

  display() {
    const formattedTime = this.timestamp.toISOString().replace(/T/, ' ').replace(/\..+/, '');
    console.log(`[ERROR] [Code: ${this.errorCode}] [${formattedTime}] Message: ${this.message}`);
  }

  getErrorCode() {
    return this.errorCode;
  }
}

