```javascript
function areArraysEqual(arr1, arr2) {
  if (!Array.isArray(arr1) || !Array.isArray(arr2)) {
    // Handle non-array inputs if necessary, or throw an error
    return false;
  }

  if (arr1.length !== arr2.length) {
    return false; // Arrays must have the same length
  }

  for (let i = 0; i < arr1.length; i++) {
    if (arr1[i] !== arr2[i]) {
      return false; // Elements at corresponding positions must be strictly equal
    }
  }

  return true; // All checks passed
}

// Test cases:
console.log(areArraysEqual([1, 2, 3], [1, 2, 3]));          // true
console.log(areArraysEqual([1, 2, '3'], [1, 2, 3]));        // false (type mismatch for '3' vs 3)
console.log(areArraysEqual([1, 2], [1, 2, 3]));             // false (different lengths)
console.log(areArraysEqual([], []));                       // true
console.log(areArraysEqual([null, undefined], [null, undefined])); // true
console.log(areArraysEqual([NaN], [NaN]));                   // false (NaN !== NaN)
console.log(areArraysEqual([1, {a:1}], [1, {a:1}]));         // false (objects by reference)
console.log(areArraysEqual([1, 'a', true], [1, 'a', true])); // true
```

