      if (typeof productName === 'string' && productName.length > 0 && typeof price === 'number' && price > 0) {
        items.push({ name: productName, price: price });
        console.log(`${productName} added to cart.`);
      } else {
        console.error('Invalid product name or price.');
      }
    },
    getTotal: function() {
      return items.reduce((total, item) => total + item.price, 0);
    },
    getCartItems: function() {
      // Return a shallow copy to prevent external modification of the private array
      return [...items]; 
      // Or: return items.slice();
    },
    removeItem: function(productName) {
        const initialLength = items.length;
        items = items.filter(item => item.name !== productName);
        if (items.length < initialLength) {
            console.log(`${productName} removed from cart.`);
        } else {
            console.log(`${productName} not found in cart.`);
        }
    }
  };
}

// Demonstrate usage:
