myCart.addItem('Mouse', 25);
myCart.addItem('Keyboard', 75);

console.log('Current items:', myCart.getCartItems()); // Should show Laptop, Mouse, Keyboard
console.log('Total:', myCart.getTotal()); // Expected: 1300

