function areEqual(a, b, strict) {
  // Handle NaN case first for strict comparison
  if (Number.isNaN(a) && Number.isNaN(b)) {
    return true;
  }

  if (strict) {
    // Strict comparison for primitives and simple types
    if (a === b) {
      return true;
    }

    // Handle null explicitly, as typeof null is 'object'
    if (a === null || b === null) {
        return false; // Already handled null === null by a === b
    }

    // Deep comparison for objects and arrays
    if (typeof a === 'object' && typeof b === 'object') {
      // Array comparison
      if (Array.isArray(a) && Array.isArray(b)) {
        if (a.length !== b.length) {
          return false;
        }
        for (let i = 0; i < a.length; i++) {
          // Recursively call areEqual for elements (deep comparison)
          if (!areEqual(a[i], b[i], true)) {
            return false;
          }
        }
        return true;
      }

      // Plain object comparison
      if (!Array.isArray(a) && !Array.isArray(b)) {
        const keysA = Object.keys(a);
        const keysB = Object.keys(b);

        if (keysA.length !== keysB.length) {
          return false;
        }

        for (let key of keysA) {
          if (!keysB.includes(key) || !areEqual(a[key], b[key], true)) {
            return false;
          }
        }
        return true;
      }
    }

    return false; // Default for strict if types or values don't match after specific checks

  } else {
    // Loose comparison for all types
    return a == b;
  }
}

// Test Cases:
console.log('--- Loose Equality ---');
console.log(`1 == '1' (false): ${areEqual(1, '1', false)}`);           // Expected: true
console.log(`null == undefined (false): ${areEqual(null, undefined, false)}`); // Expected: true
console.log(`0 == false (false): ${areEqual(0, false, false)}`);         // Expected: true
console.log(`[] == 0 (false): ${areEqual([], 0, false)}`);             // Expected: true
console.log(`{} == {} (false): ${areEqual({}, {}, false)}`);          // Expected: false (reference)

console.log('\n--- Strict Equality ---');
console.log(`1 === '1' (true): ${areEqual(1, '1', true)}`);           // Expected: false
console.log(`null === undefined (true): ${areEqual(null, undefined, true)}`); // Expected: false
console.log(`0 === false (true): ${areEqual(0, false, true)}`);         // Expected: false
console.log(`[] === [] (true): ${areEqual([], [], true)}`);             // Expected: true (deep, array)
console.log(`{} === {} (true): ${areEqual({}, {}, true)}`);             // Expected: true (deep, object)
console.log(`NaN === NaN (true): ${areEqual(NaN, NaN, true)}`);           // Expected: true (special NaN handling)

console.log('\n--- Strict Deep Array Equality ---');
console.log(`[1, 2] vs [1, 2]: ${areEqual([1, 2], [1, 2], true)}`);           // Expected: true
console.log(`[1, 2, [3, 4]] vs [1, 2, [3, 4]]: ${areEqual([1, 2, [3, 4]], [1, 2, [3, 4]], true)}`); // Expected: true
console.log(`[1, 2, [3, 4]] vs [1, 2, [3, '4']]: ${areEqual([1, 2, [3, 4]], [1, 2, [3, '4']], true)}`); // Expected: false (type mismatch '4' vs 4)
console.log(`[1, 2] vs [1, '2']: ${areEqual([1, 2], [1, '2'], true)}`);       // Expected: false (type mismatch '2' vs 2)

console.log('\n--- Strict Deep Object Equality ---');
console.log(`{ a: 1, b: 2 } vs { a: 1, b: 2 }: ${areEqual({ a: 1, b: 2 }, { a: 1, b: 2 }, true)}`); // Expected: true
console.log(`{ a: 1, b: { c: 3 } } vs { a: 1, b: { c: 3 } }: ${areEqual({ a: 1, b: { c: 3 } }, { a: 1, b: { c: 3 } }, true)}`); // Expected: true
console.log(`{ b: 2, a: 1 } vs { a: 1, b: 2 }: ${areEqual({ b: 2, a: 1 }, { a: 1, b: 2 }, true)}`); // Expected: true (key order doesn't matter)
console.log(`{ a: 1 } vs { a: '1' }: ${areEqual({ a: 1 }, { a: '1' }, true)}`); // Expected: false (type mismatch)
console.log(`{ a: 1, b: [2] } vs { a: 1, b: [2] }: ${areEqual({ a: 1, b: [2] }, { a: 1, b: [2] }, true)}`); // Expected: true (nested array)

