function createEventEmitter() {
  // `listeners` is a private variable, encapsulated by the closure.
  // It stores events as keys and arrays of listener functions as values.
  const listeners = {}; 

  return {
    /**
     * Registers a listener function for a specific event.
     * @param {string} eventName - The name of the event.
     * @param {Function} listener - The function to call when the event is emitted.
     */
    on: function(eventName, listener) {
      if (typeof listener !== 'function') {
        console.error('Listener must be a function.');
        return;
      }
      if (!listeners[eventName]) {
        listeners[eventName] = [];
      }
      listeners[eventName].push(listener);
      console.log(`Listener added for event: ${eventName}`);
    },

    /**
     * Emits an event, calling all registered listeners with provided arguments.
     * @param {string} eventName - The name of the event to emit.
     * @param {...any} args - Arguments to pass to the listeners.
     */
    emit: function(eventName, ...args) {
      const eventListeners = listeners[eventName];
      if (eventListeners) {
        console.log(`Emitting event: ${eventName} with args:`, args);
        eventListeners.forEach(listener => {
          listener(...args);
        });
      } else {
        console.log(`No listeners registered for event: ${eventName}`);
      }
    },

    /**
     * (Optional, but good for completeness): Removes a specific listener for an event.
     * @param {string} eventName - The name of the event.
     * @param {Function} listenerToRemove - The specific listener function to remove.
     */
    off: function(eventName, listenerToRemove) {
        if (listeners[eventName]) {
            listeners[eventName] = listeners[eventName].filter(listener => listener !== listenerToRemove);
            console.log(`Listener removed for event: ${eventName}`);
        }
    }
  };
}

// Demonstrate usage:
const myEmitter = createEventEmitter();

// Register listeners
const welcomeListener = (username) => {
  console.log(`Welcome, ${username}!`);
};

const notifyAdminListener = (username) => {
  console.log(`Admin notified: ${username} logged in.`);
};

myEmitter.on('userLoggedIn', welcomeListener);
myEmitter.on('userLoggedIn', notifyAdminListener);

myEmitter.on('paymentSuccessful', (amount, user) => {
  console.log(`Payment of $${amount} successful for ${user}.`);
});

// Emit events
console.log('\n--- Emitting userLoggedIn ---');
myEmitter.emit('userLoggedIn', 'Alice');

console.log('\n--- Emitting paymentSuccessful ---');
myEmitter.emit('paymentSuccessful', 99.99, 'Bob');

console.log('\n--- Emitting unknown event ---');
myEmitter.emit('orderPlaced'); // No listeners, should log accordingly

console.log('\n--- Removing a listener ---');
myEmitter.off('userLoggedIn', notifyAdminListener);

console.log('\n--- Emitting userLoggedIn again after removal ---');
myEmitter.emit('userLoggedIn', 'Charlie'); // Only Welcome, Charlie! should be logged
