import React, { useState } from 'react';

// Child Component 1: Displays the count
function DisplayCount({ count }) {
  return <h2>Current Count: {count}</h2>;
}

// Child Component 2: Button to increment the count
function CounterButton({ onIncrement }) {
  return (
    <button onClick={onIncrement}>
      Increment Count
    </button>
  );
}

// Parent Component: Manages the shared count state
function CounterApp() {
  const [count, setCount] = useState(0);

  const handleIncrement = () => {
    setCount(prevCount => prevCount + 1);
  };

  return (
    <div>
      <h1>Lifting State Up Example</h1>
      {/* State passed down as prop */}
      <DisplayCount count={count} />
      {/* Callback function passed down as prop */}
      <CounterButton onIncrement={handleIncrement} />
    </div>
  );
}

// Usage example:
