import React, { useState, useRef } from 'react';

function DraggableBox() {
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const offset = useRef({ x: 0, y: 0 }); // To store the offset from mouse to element top-left
  const boxRef = useRef(null);

  const handlePointerDown = (e) => {
    setIsDragging(true);
    // Calculate the offset from the pointer's current position to the box's top-left corner
    // This prevents the box from 'jumping' its top-left to the pointer's position on click.
    const rect = boxRef.current.getBoundingClientRect();
    offset.current = {
      x: e.clientX - rect.left,
      y: e.clientY - rect.top,
    };
    // Important for dragging: capture the pointer to receive events even if pointer leaves the element
    boxRef.current.setPointerCapture(e.pointerId);
  };

  const handlePointerMove = (e) => {
    if (isDragging) {
      // Update the position based on pointer movement and stored offset
      setPosition({
        x: e.clientX - offset.current.x,
        y: e.clientY - offset.current.y,
      });
    }
  };

  const handlePointerUp = (e) => {
    setIsDragging(false);
    // Release pointer capture
    boxRef.current.releasePointerCapture(e.pointerId);
  };

  return (
    <div
      ref={boxRef}
      style={{
        position: 'absolute',
        width: '100px',
        height: '100px',
        backgroundColor: 'rebeccapurple',
        color: 'white',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        cursor: isDragging ? 'grabbing' : 'grab',
        left: position.x + 'px',
        top: position.y + 'px',
        userSelect: 'none',
        touchAction: 'none' // Prevents browser default touch actions like scrolling
      }}
      onPointerDown={handlePointerDown}
      onPointerMove={handlePointerMove}
      onPointerUp={handlePointerUp}
      // onPointerLeave={isDragging ? handlePointerUp : undefined} // Optional: useful if drag ends by leaving window
      // onPointerCancel={handlePointerUp} // Also good to handle cancel events
    >
      Drag Me
    </div>
  );
}

export default DraggableBox;

