import React from 'react';

class MessageToggler extends React.Component {
  // Initialize state using class fields syntax
  state = {
    isMessageVisible: false
  };

  // Method to handle the button click and toggle state
  handleToggleClick = () => {
    // Use the functional form of setState for toggling boolean values
    this.setState(prevState => ({
      isMessageVisible: !prevState.isMessageVisible
    }));
  };

  render() {
    const { isMessageVisible } = this.state;

    return (
      <div>
        <button onClick={this.handleToggleClick}>
          {isMessageVisible ? 'Hide Message' : 'Show Message'}
        </button>
        {isMessageVisible && (
          <p>This is the secret message! You found it!</p>
        )}
      </div>
    );
  }
}

export default MessageToggler;

