import React, { useState } from 'react';

// Parent component holds the shared state
function TemperatureCalculator() {
  const [temperature, setTemperature] = useState('');
  const [scale, setScale] = useState('c');

  // Conversion functions
  const toCelsius = (fahrenheit) => {
    if (isNaN(parseFloat(fahrenheit))) return '';
    return ((parseFloat(fahrenheit) - 32) * 5 / 9).toFixed(2);
  };
  const toFahrenheit = (celsius) => {
    if (isNaN(parseFloat(celsius))) return '';
    return ((parseFloat(celsius) * 9 / 5) + 32).toFixed(2);
  };

  // Handle temperature change from Celsius input
  const handleCelsiusChange = (value) => {
    setTemperature(value);
    setScale('c');
  };

  // Handle temperature change from Fahrenheit input
  const handleFahrenheitChange = (value) => {
    setTemperature(value);
    setScale('f');
  };

  // Calculate both temperatures based on current scale
  const celsius = scale === 'f' ? toCelsius(temperature) : temperature;
  const fahrenheit = scale === 'c' ? toFahrenheit(temperature) : temperature;

  return (
    <div>
      <h2>Temperature Converter</h2>
      <TemperatureInput 
        scale="c" 
        temperature={celsius}
        onTemperatureChange={handleCelsiusChange} 
      />
      <TemperatureInput 
        scale="f" 
        temperature={fahrenheit}
        onTemperatureChange={handleFahrenheitChange} 
      />
      <BoilingVerdict celsius={parseFloat(celsius)} />
    </div>
  );
}

// Child component receives props from parent
function TemperatureInput({ scale, temperature, onTemperatureChange }) {
  const scaleNames = {
    c: 'Celsius',
    f: 'Fahrenheit'
  };

  const handleChange = (e) => {
    onTemperatureChange(e.target.value);
  };

  return (
    <fieldset>
      <legend>Enter temperature in {scaleNames[scale]}:</legend>
      <input 
        value={temperature}
        onChange={handleChange} 
      />
    </fieldset>
  );
}

// Another component that uses the same state
function BoilingVerdict({ celsius }) {
  if (isNaN(celsius)) {
    return <p>Enter a number to see boiling verdict.</p>;
  }
  return celsius >= 100 ?
    <p style={{ color: 'green' }}>The water would boil.</p> :
    <p style={{ color: 'blue' }}>The water would not boil.</p>;
}

// To render:
// ReactDOM.render(<TemperatureCalculator />, document.getElementById('root'));

import React from 'react';

class MyForm extends React.Component {
  constructor(props) {
    super(props);
    this.textInput = React.createRef(); // Create a ref
  }

  focusTextInput = () => {
    // Use the DOM API to focus the input
    this.textInput.current.focus();
  };

  render() {
    return (
      <div>
        <input
          type="text"
          ref={this.textInput} // Attach the ref to the input element
        />
        <button onClick={this.focusTextInput}>
          Focus the text input
        </button>
      </div>
    );
  }
}
