import React, { useState } from 'react';

// Parent component: ShoppingCart
function ShoppingCart() {
  const [quantities, setQuantities] = useState({
    'Laptop': 1,
    'Mouse': 2,
    'Keyboard': 0
  });

  const handleQuantityChange = (productName, change) => {
    setQuantities(prevQuantities => {
      const newQuantity = Math.max(0, prevQuantities[productName] + change);
      return {
        ...prevQuantities,
        [productName]: newQuantity
      };
    });
  };

  const totalItems = Object.values(quantities).reduce((sum, qty) => sum + qty, 0);

  return (
    <div>
      <h1>Your Shopping Cart</h1>
      <ProductItem 
        productName="Laptop" 
        quantity={quantities['Laptop']} 
        onQuantityChange={handleQuantityChange} 
      />
      <ProductItem 
        productName="Mouse" 
        quantity={quantities['Mouse']} 
        onQuantityChange={handleQuantityChange} 
      />
      <ProductItem 
        productName="Keyboard" 
        quantity={quantities['Keyboard']} 
        onQuantityChange={handleQuantityChange} 
      />
      <TotalItems total={totalItems} />
    </div>
  );
}

// Child component: ProductItem
function ProductItem({ productName, quantity, onQuantityChange }) {
  return (
    <div style={{ border: '1px solid #ccc', padding: '10px', margin: '10px', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
      <span>{productName}</span>
      <div>
        <button onClick={() => onQuantityChange(productName, -1)} disabled={quantity <= 0}>-</button>
        <span style={{ margin: '0 10px' }}>{quantity}</span>
        <button onClick={() => onQuantityChange(productName, 1)}>+</button>
      </div>
    </div>
  );
}

// Another child component: TotalItems
function TotalItems({ total }) {
  return (
    <h3 style={{ marginTop: '20px', borderTop: '1px solid #eee', paddingTop: '10px' }}>Total Items in Cart: {total}</h3>
