import React, { useRef, useEffect } from 'react';

function SearchBar() {
  const inputRef = useRef(null); // Create ref for the input

  useEffect(() => {
    // Auto-focus on mount
    if (inputRef.current) {
      inputRef.current.focus();
    }
  }, []); // Empty dependency array ensures this runs once after mount

  const handleClearAndFocus = () => {
    if (inputRef.current) {
      inputRef.current.value = ''; // Clear input value
      inputRef.current.focus();   // Re-focus
    }
  };

  return (
    <div>
      <input
        type="text"
        placeholder="Search..."
        ref={inputRef} // Attach ref
      />
      <button onClick={handleClearAndFocus}>Clear & Focus</button>
    </div>
  );
}

export default SearchBar;

