import React, { useRef, useEffect, useState } from 'react';

function Stopwatch() {
  const intervalRef = useRef(null); // Used to store interval ID
  const [seconds, setSeconds] = useState(0);

  useEffect(() => {
    // Start interval and store its ID in intervalRef.current
    intervalRef.current = setInterval(() => {
      setSeconds(prevSeconds => prevSeconds + 1);
    }, 1000);

    return () => {
      // Cleanup: Clear interval when component unmounts
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, []); // Empty dependency array for componentDidMount-like behavior

  const stopTimer = () => {
    if (intervalRef.current) {
      clearInterval(intervalRef.current);
      intervalRef.current = null; // Important to nullify after clearing
    }
  };

  const resetTimer = () => {
    stopTimer(); // Reuse stop logic
    setSeconds(0);
  };

  return (
    <div>
      <h1>Stopwatch: {seconds}s</h1>
      <button onClick={stopTimer}>Stop</button>
      <button onClick={resetTimer}>Reset</button>
    </div>
  );
}

export default Stopwatch;

