import React, { useState } from 'react';

class ErrorBoundary extends React.Component<any, any> {
  state = { hasError: false, errorInfo: null };
  constructor(props: any) {
    super(props);
    // Adding a name prop to distinguish boundaries in logs
    // This is not part of standard ErrorBoundary but useful for this task
    this.state = { hasError: false, errorInfo: null };
  }

  static getDerivedStateFromError(error: Error) {
    console.log(`ErrorBoundary (${this.name || 'Unnamed'}): getDerivedStateFromError called.`);
    return { hasError: true };
  }

  componentDidCatch(error: Error, errorInfo: React.ErrorInfo) {
    console.error(`ErrorBoundary (${(this.props as any).name || 'Unnamed'}): componentDidCatch caught error:`, error.message, errorInfo.componentStack);
    this.setState({ errorInfo });
  }

  render() {
    if (this.state.hasError) {
      return (
        <div style={{ border: '2px dashed red', padding: '15px', margin: '10px 0', backgroundColor: '#ffe6e6' }}>
          <h3> Component Failed! (Boundary: {(this.props as any).name || 'Unnamed'})</h3>
          {this.state.errorInfo && (
            <details>
              <summary>Error Details</summary>
              <pre style={{ whiteSpace: 'pre-wrap', fontSize: '0.75em', maxHeight: '150px', overflowY: 'auto' }}>
                {this.state.errorInfo.componentStack}
              </pre>
            </details>
          )}
        </div>
      );
    }
    return this.props.children;
  }
}

const Header: React.FC = () => (
  <header style={{ background: '#f0f0f0', padding: '20px', textAlign: 'center' }}>
    <h2>Application Header</h2>
  </header>
