  );
};

const WidgetB: React.FC<{ triggerError: boolean }> = ({ triggerError }) => {
  if (triggerError) {
    throw new Error('Error in WidgetB!');
  }
  return (
    <div style={{ border: '1px solid lightgreen', padding: '10px', margin: '5px' }}>
      <h4>Widget B</h4>
      <p>This widget is also fine.</p>
    </div>
  );
};

const MainContent: React.FC = () => {
