import React, { useState } from 'react';

// Basic Error Boundary (for demonstrating its non-involvement)
class GenericErrorBoundary extends React.Component<any, any> {
  state = { hasError: false };
  static getDerivedStateFromError(error: Error) {
    console.log('GenericErrorBoundary caught a render error:', error.message);
    return { hasError: true };
  }
  componentDidCatch(error: Error, errorInfo: React.ErrorInfo) {
    console.error('GenericErrorBoundary componentDidCatch (render error):', error, errorInfo.componentStack);
  }
  render() {
    if (this.state.hasError) {
      return <div style={{ border: '2px solid purple', padding: '10px', margin: '10px' }}>
               <p>A rendering error occurred in a child. (Caught by GenericErrorBoundary)</p>
             </div>;
    }
    return this.props.children;
  }
}

const ActionButtons: React.FC = () => {
  const [status, setStatus] = useState<string>('Ready');

  const handleSyncError = () => {
    try {
      // Error in event handler - caught by local try/catch
      throw new Error('Simulated synchronous error in onClick!');
    } catch (error: any) {
      console.error('Locally caught sync error:', error.message);
      setStatus(`Sync Error Handled: ${error.message}`);
      // Optionally, show a temporary error message in the UI
    }
  };

  const handleAsyncError = () => {
    setStatus('Attempting async error...');
    // Error in async code (setTimeout callback) - needs try/catch inside callback
    setTimeout(() => {
      try {
        throw new Error('Simulated asynchronous error in setTimeout!');
      } catch (error: any) {
        console.error('Locally caught async error:', error.message);
        setStatus(`Async Error Handled: ${error.message}`);
      }
    }, 1000);

    // Example with Promise rejection
    /*
    new Promise((_, reject) => {
      setTimeout(() => {
        reject(new Error('Simulated async promise rejection!'));
      }, 1500);
    })
    .catch((error: any) => {
      console.error('Locally caught promise error:', error.message);
      setStatus(`Promise Error Handled: ${error.message}`);
    });
    */
  };

  // Example of an error that WOULD be caught by GenericErrorBoundary
  const triggerRenderError = () => {
    // Simulate a rendering error for the ErrorBoundary to catch
    throw new Error('Simulated rendering error!');
  };

  return (
    <div style={{ border: '1px solid gray', padding: '20px', margin: '20px' }}>
      <h3>Action Buttons with Local Error Handling</h3>
      <button onClick={handleSyncError} style={{ marginRight: '10px' }}>
        Trigger Sync Error (Event Handler)
      </button>
      <button onClick={handleAsyncError} style={{ marginRight: '10px' }}>
        Trigger Async Error (setTimeout)
      </button>
      {/* Optional button to demonstrate a render error caught by GenericErrorBoundary */}
      <button onClick={triggerRenderError}>Trigger Render Error (for ErrorBoundary)</button>
      <p style={{ marginTop: '15px', fontWeight: 'bold' }}>Status: {status}</p>
    </div>
  );
};

const App: React.FC = () => {
  return (
    <div>
      <h1>Error Handling Outside Boundaries Task</h1>
      <GenericErrorBoundary>
        <ActionButtons />
      </GenericErrorBoundary>
      <p>Application continues to run.</p>
    </div>
  );
};

export default App;
