import { createSelector } from 'reselect';

// Input selectors
const getUsers = (state) => state.users.list;
const getActiveFilter = (state) => state.filters.active;

// Memoized selector for filtered users
export const selectFilteredUsers = createSelector(
  [getUsers, getActiveFilter],
  (users, activeFilter) => {
    if (activeFilter === 'all') {
      return users;
    }
    return users.filter(user => user.status === activeFilter);
  }
);

