import { createSelector } from 'reselect';

// --- Dummy Redux State Structure ---
const dummyState = {
  cart: {
    items: [
      { id: 'p1', name: 'Laptop', price: 1200, quantity: 1 },
      { id: 'p2', name: 'Mouse', price: 25, quantity: 2 },
      { id: 'p3', name: 'Keyboard', price: 75, quantity: 1 }
    ]
  },
  user: { name: 'John Doe' },
  settings: { theme: 'dark' }
};

// --- Your Selectors Here ---

// 1. Basic selector for raw cart items
export const selectCartItems = (state) => {
  // console.log('Running selectCartItems'); // For debugging memoization
  return state.cart.items;
};

// 2. Memoized selector for total quantity
export const selectTotalItemsInCart = createSelector(
  [selectCartItems],
  (items) => {
    console.log('Recalculating total items...'); // Monitor re-computation
    return items.reduce((total, item) => total + item.quantity, 0);
  }
);

// 3. Memoized selector for total price
export const selectTotalPrice = createSelector(
  [selectCartItems],
  (items) => {
    console.log('Recalculating total price...'); // Monitor re-computation
    return items.reduce((total, item) => total + (item.price * item.quantity), 0);
  }
);

// 4. Memoized selector for cart summary
export const selectCartSummary = createSelector(
  [selectTotalItemsInCart, selectTotalPrice],
  (totalItems, totalPrice) => {
    console.log('Recalculating cart summary...'); // Monitor re-computation
    return { totalItems, totalPrice };
  }
);

// --- Test Calls ---
console.log('--- Initial State Calculation ---');
console.log('Cart Items:', selectCartItems(dummyState));
console.log('Total Items:', selectTotalItemsInCart(dummyState));
console.log('Total Price:', selectTotalPrice(dummyState));
console.log('Cart Summary:', selectCartSummary(dummyState));

console.log('\n--- Changing non-cart state (user name) ---');
const dummyState2 = { ...dummyState, user: { name: 'Jane Doe' } };
// These should NOT log 'Recalculating...' because input selectors (selectCartItems, selectTotalItemsInCart, selectTotalPrice)
// didn't change their output reference.
console.log('Total Items (after user change):', selectTotalItemsInCart(dummyState2));
console.log('Total Price (after user change):', selectTotalPrice(dummyState2));
console.log('Cart Summary (after user change):', selectCartSummary(dummyState2));

console.log('\n--- Changing cart items state ---');
const dummyState3 = {
  ...dummyState,
  cart: {
    items: [...dummyState.cart.items, { id: 'p4', name: 'Webcam', price: 50, quantity: 1 }]
  }
};
// These SHOULD log 'Recalculating...' because selectCartItems' output reference changes.
console.log('Total Items (after cart change):', selectTotalItemsInCart(dummyState3));
console.log('Total Price (after cart change):', selectTotalPrice(dummyState3));
console.log('Cart Summary (after cart change):', selectCartSummary(dummyState3));

