tasks.solutionCode:
import { List } from 'immutable';

const initialPreferences = List(['Sports', 'Technology']);

// 1. Function to add a category
function addCategory(preferencesList, categoryName) {
  if (preferencesList.includes(categoryName)) {
    return preferencesList; // Category already exists, return original list for immutability
  }
  return preferencesList.push(categoryName);
}

// 2. Function to remove a category
function removeCategory(preferencesList, categoryName) {
  const index = preferencesList.indexOf(categoryName);
  if (index === -1) {
    return preferencesList; // Category not found, return original list
  }
  return preferencesList.delete(index);
}

// 3. Function to toggle a category (add if not present, remove if present)
function toggleCategory(preferencesList, categoryName) {
  if (preferencesList.includes(categoryName)) {
    return removeCategory(preferencesList, categoryName);
  } else {
    return addCategory(preferencesList, categoryName);
  }
}

// 4. Function to get a category by index
function getCategoryByIndex(preferencesList, index) {
  return preferencesList.get(index);
}

// --- Test Cases ---
console.log('Initial:', initialPreferences.toJS()); // [ 'Sports', 'Technology' ]

// Test Add
const addedList = addCategory(initialPreferences, 'News');
console.log('After adding News:', addedList.toJS()); // [ 'Sports', 'Technology', 'News' ]
console.log('Original unchanged:', initialPreferences.toJS()); // [ 'Sports', 'Technology' ]
const addedExistingList = addCategory(addedList, 'Sports');
console.log('After adding existing Sports (should be same as addedList):', addedExistingList.toJS()); // [ 'Sports', 'Technology', 'News' ]

// Test Remove
const removedList = removeCategory(addedList, 'Technology');
console.log('After removing Technology from addedList:', removedList.toJS()); // [ 'Sports', 'News' ]
const removedNonExistent = removeCategory(removedList, 'Politics');
console.log('After removing non-existent Politics (should be same as removedList):', removedNonExistent.toJS()); // [ 'Sports', 'News' ]

// Test Toggle
const toggledAddList = toggleCategory(removedList, 'Politics');
console.log('After toggling (add) Politics:', toggledAddList.toJS()); // [ 'Sports', 'News', 'Politics' ]
const toggledRemoveList = toggleCategory(toggledAddList, 'Sports');
console.log('After toggling (remove) Sports:', toggledRemoveList.toJS()); // [ 'News', 'Politics' ]

// Test Get By Index
console.log('Category at index 0 of initial:', getCategoryByIndex(initialPreferences, 0)); // Sports
console.log('Category at index 1 of initial:', getCategoryByIndex(initialPreferences, 1)); // Technology
console.log('Category at invalid index:', getCategoryByIndex(initialPreferences, 100)); // undefined

