import { List } from 'immutable';

let list = List([1, 3, 2, 4, 5]);

console.log('Original list:', list.toJS()); // [1, 3, 2, 4, 5]
console.log('List size:', list.size); // 5

list = list.pop().pop(); // Removes 5, then 4
console.log('After two pops:', list.toJS()); // [1, 3, 2]

list = list.push(6);
console.log('After push(6):', list.toJS()); // [1, 3, 2, 6]

list = list.shift();
console.log('After shift():', list.toJS()); // [3, 2, 6]

list = list.concat([9, 0, 1, 4]); // Can take multiple args or an array
console.log('After concat(9, 0, 1, 4):', list.toJS()); // [3, 2, 6, 9, 0, 1, 4]

list = list.sort();
console.log('After sort():', list.toJS()); // [0, 1, 2, 3, 4, 6, 9]

const originalListAfterOps = List([1, 3, 2, 4, 5]);
console.log('Original list unchanged:', originalListAfterOps.toJS()); // [1, 3, 2, 4, 5]

import { Stack } from 'immutable';

let stack = new Stack();

console.log('Initial stack:', stack.toJS()); // []

// Push multiple elements. They are added in order, but the last pushed is at index 0 (top)
stack = stack.push(2, 1, 0); 
console.log('After push(2, 1, 0):', stack.toJS()); // [0, 1, 2] -- Note: `toJS()` might reverse for display convenience, internal order is 0 (top), 1, 2

console.log('Stack size:', stack.size); // 3

// get(0) returns the top element (the last one pushed)
console.log('stack.get(0):', stack.get(0)); // 0 
console.log('stack.get(1):', stack.get(1)); // 1
console.log('stack.get(2):', stack.get(2)); // 2

// Pop removes the top element (0)
stack = stack.pop(); 
console.log('After pop():', stack.toJS()); // [1, 2]

const originalStack = new Stack().push(2,1,0);
console.log('Original stack unchanged:', originalStack.toJS()); // [0, 1, 2]

import { Map } from 'immutable';

let map = new Map();

console.log('Initial map:', map.toJS()); // {}

map = map.set('value', 5);
console.log('After set(\'value\', 5):', map.toJS()); // {value: 5}

map = map.set('text', 'Test');
console.log('After set(\'text\', \'Test\'):', map.toJS()); // {value: 5, text: "Test"}

map = map.delete('text');
console.log('After delete(\'text\'):', map.toJS()); // {value: 5}

const originalMap = new Map().set('value', 5).set('text', 'Test');
console.log('Original map unchanged:', originalMap.toJS()); // {value: 5, text: "Test"}

console.log('Value of \'value\':', map.get('value')); // 5
console.log('Has key \'value\':', map.has('value')); // true
console.log('Has key \'text\':', map.has('text')); // false

import { OrderedMap } from 'immutable';

let map = new OrderedMap();

map = map.set('m', 5);
map = map.set('a', 1);
map = map.set('p', 8);

console.log('OrderedMap content:', map.toJS()); // { m: 5, a: 1, p: 8 }

console.log('Iteration order:');
// Iteration order will be: ['m', 5], ['a', 1], ['p', 8]
for(const elem of map) {
  console.log(elem); // Prints [ 'm', 5 ], [ 'a', 1 ], [ 'p', 8 ] in order
}

// Updating a value doesn't change its position
map = map.set('a', 100);
console.log('After updating \'a\':', map.toJS()); // { m: 5, a: 100, p: 8 }
console.log('Iteration order after update:');
for(const elem of map) {
  console.log(elem); // Still [ 'm', 5 ], [ 'a', 100 ], [ 'p', 8 ] in order
}

import { Set } from 'immutable';

var s1 = Set([2, 1]);
var s2 = Set([2, 3, 3]); // Duplicates are removed here
var s3 = Set([1, 1, 1]);

console.log('s1 (from [2, 1]):', s1.toJS()); // [1, 2] (order not guaranteed, often sorted by JS engine)
console.log('s2 (from [2, 3, 3]):', s2.toJS()); // [2, 3]
console.log('s3 (from [1, 1, 1]):', s3.toJS()); // [1]

console.log('Count s1, s2, s3:', s1.count(), s2.size, s3.count()); // 2 2 1

// toJS(), toArray(), toJSON() are aliases for Set
console.log('s1.toJS():', s1.toJS()); // [1, 2]
console.log('s2.toArray():', s2.toArray()); // [2, 3]
console.log('s3.toJSON():', s3.toJSON()); // [1]

var s1S2Intersect = s1.intersect(s2);
console.log('s1 intersect s2:', s1S2Intersect.toJSON()); // [2]

console.log('s1 has 1:', s1.has(1)); // true
console.log('s1 has 5:', s1.has(5)); // false

const originalS1 = Set([2, 1]);
console.log('Original s1 unchanged:', originalS1.toJS()); // [1, 2]

import { OrderedSet } from 'immutable';

var s1 = OrderedSet([2, 1]);
var s2 = OrderedSet([2, 3, 3]); // Duplicates are removed, order from [2, 3]
var s3 = OrderedSet([1, 1, 1]); // Duplicates removed, order from [1]

console.log('s1 (OrderedSet([2, 1])):', s1.toJS()); // [2, 1]
console.log('s2 (OrderedSet([2, 3, 3])):', s2.toJS()); // [2, 3]
console.log('s3 (OrderedSet([1, 1, 1])):', s3.toJS()); // [1]

// Union operation combines unique elements, preserving first seen order
var s1S2S3Union = s1.union(s2, s3);
console.log('s1 union s2 union s3:', s1S2S3Union.toJSON()); // [2, 1, 3]

// Explanation of union result: 
// From s1: 2, 1
// From s2: 2 (already seen), 3 (new, added after 1)
// From s3: 1 (already seen)
// Result: [2, 1, 3]

const originalS1 = OrderedSet([2, 1]);
console.log('Original s1 unchanged:', originalS1.toJS()); // [2, 1]

