```typescript
import { Map } from 'immutable';

function mergeUserData(
  initialData: Map<string, any>,
  updates: Map<string, any>
): Map<string, any> {
  // The merge method correctly combines maps, with the latter taking precedence for conflicts
  return initialData.merge(updates);
}

// Example usage:
const currentUser = Map({
  id: 'user123',
  name: 'Alice',
  email: 'alice@example.com',
  settings: Map({ theme: 'dark', notifications: true })
});

const partialUpdate = Map({
  email: 'alice.smith@example.com',
  status: 'active',
  settings: Map({ theme: 'light' }) // Nested map is also merged, but shallowly here
});

const updatedUser = mergeUserData(currentUser, partialUpdate);

console.log('Original User:', currentUser.toJS());
/*
{ 
  id: 'user123',
  name: 'Alice',
  email: 'alice@example.com',
  settings: { theme: 'dark', notifications: true }
}
*/

console.log('Updated User:', updatedUser.toJS());
/*
{ 
  id: 'user123',
  name: 'Alice',
  email: 'alice.smith@example.com',
  status: 'active',
  settings: { theme: 'light' } // Note: settings map is replaced entirely if not deep merged
}
*/

// If deep merge is desired for nested maps, use mergeDeep
const deepUpdatedUser = currentUser.mergeDeep(partialUpdate);
console.log('Deep Merged User:', deepUpdatedUser.toJS());
/*
{ 
  id: 'user123',
  name: 'Alice',
  email: 'alice.smith@example.com',
  status: 'active',
  settings: { theme: 'light', notifications: true } // 'notifications' is preserved
}
*/
