import { List, Map } from 'immutable';

interface Product { 
  id: string; 
  name: string; 
  price: number; 
  inStock: boolean; 
}

const initialProducts: Product[] = [
  { id: 'p1', name: 'Laptop', price: 1200, inStock: true },
  { id: 'p2', name: 'Mouse', price: 25, inStock: true },
  { id: 'p3', name: 'Keyboard', price: 75, inStock: false }
];

// 1. Function to initialize immutable catalog
function initializeCatalog(products: Product[]): List<Map<string, any>> {
  return List(products.map(product => Map(product)));
}

// 2. Function to add a product
function addProduct(catalog: List<Map<string, any>>, newProduct: Product): List<Map<string, any>> {
  return catalog.push(Map(newProduct));
}

// 3. Function to update product price
function updateProductPrice(catalog: List<Map<string, any>>, productId: string, newPrice: number): List<Map<string, any>> {
  const index = catalog.findIndex(product => product.get('id') === productId);
  if (index !== -1) {
    return catalog.update(index, product => product.set('price', newPrice));
  }
  return catalog; // Return original if not found
}

// 4. Function to remove a product
function removeProduct(catalog: List<Map<string, any>>, productId: string): List<Map<string, any>> {
  const index = catalog.findIndex(product => product.get('id') === productId);
  if (index !== -1) {
    return catalog.delete(index);
  }
  return catalog; // Return original if not found
}

// 5. Function to convert immutable catalog to plain JS
function getJsCatalog(catalog: List<Map<string, any>>): Product[] {
  return catalog.toJS() as Product[];
}

// Example Usage (for testing your functions):
const immutableCatalog = initializeCatalog(initialProducts);
console.log('Initial Immutable Catalog (JS):', getJsCatalog(immutableCatalog));

const newProduct = { id: 'p4', name: 'Monitor', price: 300, inStock: true };
const catalogAfterAdd = addProduct(immutableCatalog, newProduct);
console.log('Catalog After Add (JS):', getJsCatalog(catalogAfterAdd));

const catalogAfterUpdate = updateProductPrice(catalogAfterAdd, 'p1', 1150);
console.log('Catalog After Update (JS):', getJsCatalog(catalogAfterUpdate));

const catalogAfterRemove = removeProduct(catalogAfterUpdate, 'p2');
console.log('Catalog After Remove (JS):', getJsCatalog(catalogAfterRemove));

// Verify original catalog is unchanged
console.log('Original Catalog Still Intact (JS):', getJsCatalog(immutableCatalog));

// Test edge cases
const catalogAfterNonExistentUpdate = updateProductPrice(catalogAfterAdd, 'non-existent', 999);
console.log('Catalog After Non-Existent Update (JS - should be same as catalogAfterAdd):', getJsCatalog(catalogAfterNonExistentUpdate));

const catalogAfterNonExistentRemove = removeProduct(catalogAfterUpdate, 'non-existent');
console.log('Catalog After Non-Existent Remove (JS - should be same as catalogAfterUpdate):', getJsCatalog(catalogAfterNonExistentRemove));

