import { Map, is, fromJS } from 'immutable';

interface UserData {
  id: string;
  name: string;
  email: string;
  preferences?: { [key: string]: any };
}

class UserProfile {
  private readonly data: Map<string, any>;

  constructor(initialData: UserData) {
    // Use fromJS for deep conversion of initial data, including nested objects/arrays
    this.data = fromJS(initialData) as Map<string, any>;
  }

  // Method to update a top-level field
  updateField(key: string, value: any): UserProfile {
    const newData = this.data.set(key, value);
    // Return a new UserProfile instance wrapping the new Immutable Map
    return new UserProfile(newData.toJS() as UserData); // Re-initialize to ensure proper type inference and instance creation
  }

  // Method to update a nested preference
  updatePreference(key: string, value: any): UserProfile {
    // Use updateIn for nested immutable updates, ensuring preferences map exists
    const newData = this.data.updateIn(
      ['preferences', key],
      val => value // This will set the value if key exists or add if not
    );
    // Return a new UserProfile instance
    return new UserProfile(newData.toJS() as UserData);
  }

  // Method to return the current user profile as a plain JavaScript object
  getProfile(): UserData {
    return this.data.toJS() as UserData;
  }

  // Method to compare two UserProfile instances efficiently using Immutable.is
  isEqual(otherProfile: UserProfile): boolean {
    return is(this.data, otherProfile.data);
  }
}

// Example Usage:
const initialUserData: UserData = {
  id: 'u1',
  name: 'John Doe',
  email: 'john.doe@example.com',
  preferences: { theme: 'dark', newsletter: true }
