import { OrderedMap, fromJS } from 'immutable';

class ConfigManager {
  private readonly config: OrderedMap<string, any>;

  constructor(initialConfig?: { [key: string]: any }) {
    // Use fromJS to convert initial plain JS object, if provided. 
    // OrderedMap constructor can also take a JS object directly.
    this.config = initialConfig ? OrderedMap(initialConfig) : OrderedMap();
  }

  addConfig(key: string, value: any): ConfigManager {
    // OrderedMap.set automatically preserves order if key exists, otherwise adds to end
    const newConfig = this.config.set(key, value);
    return new ConfigManager(newConfig.toJS()); // Re-wrap new Immutable.OrderedMap
  }

  removeConfig(key: string): ConfigManager {
    const newConfig = this.config.delete(key);
    return new ConfigManager(newConfig.toJS()); // Re-wrap new Immutable.OrderedMap
  }

  getOrderedKeys(): string[] {
    // OrderedMap.keys() returns an Iterator; convert to array
    return this.config.keySeq().toArray();
  }

  getConfig(key: string): any | undefined {
    return this.config.get(key);
  }

  toPlainObject(): { [key: string]: any } {
    // OrderedMap.toJS() preserves order when converting to plain JS object
    return this.config.toJS();
  }
}

// Example Usage:
const manager = new ConfigManager({ setting1: 'value1', setting2: 123 });
console.log('Initial Config Keys:', manager.getOrderedKeys()); // ['setting1', 'setting2']
console.log('Initial Config Object:', manager.toPlainObject()); // { setting1: 'value1', setting2: 123 }

