console.log('Config4 Object:', manager4.toPlainObject()); // { setting1: 'updatedValue', setting3: true }

console.log('Original manager keys (unchanged):', manager.getOrderedKeys()); // ['setting1', 'setting2']
console.log('Original manager object (unchanged):', manager.toPlainObject()); // { setting1: 'value1', setting2: 123 }

