import { Set } from 'immutable';

class UserTagManager {
  private readonly tags: Set<string>;

  constructor(initialTags: string[] = []) {
    this.tags = Set(initialTags);
  }

  addTag(tag: string): UserTagManager {
    const newTags = this.tags.add(tag);
    // Immutable.js returns the same instance if no change, so we can always create a new manager
    return new UserTagManager(newTags.toJS());
  }

  removeTag(tag: string): UserTagManager {
    const newTags = this.tags.delete(tag);
    return new UserTagManager(newTags.toJS());
  }

  hasTag(tag: string): boolean {
    return this.tags.has(tag);
  }

  getTags(): string[] {
    return this.tags.toJS(); // .toArray() also works
  }

  static getCommonTags(manager1: UserTagManager, manager2: UserTagManager): string[] {
    const commonSet = manager1.tags.intersect(manager2.tags);
    return commonSet.toJS();
  }
}

// Example Usage:
const user1Tags = new UserTagManager(['react', 'redux', 'javascript', 'typescript']);
console.log('User1 Tags:', user1Tags.getTags());

