tasks.solutionCode:
import { OrderedSet } from 'immutable';

class RecentHistoryManager<T> {
  private history: OrderedSet<T>;
  private readonly capacity: number;

  constructor(capacity: number, initialItems: T[] = []) {
    this.capacity = capacity;
    // Initialize history, taking first 'capacity' unique items from initialItems
    this.history = OrderedSet(initialItems.slice(0, capacity));
  }

  viewItem(item: T): RecentHistoryManager<T> {
    let newHistory = this.history;

    // If item exists, remove it first to re-add to end
    if (newHistory.has(item)) {
      newHistory = newHistory.delete(item);
    }

    // Add the item to the end (most recent)
    newHistory = newHistory.add(item);

    // If capacity is exceeded, remove the oldest item (first in the OrderedSet)
    if (newHistory.size > this.capacity) {
      newHistory = newHistory.shift(); // shift removes the first element
    }

    return new RecentHistoryManager(this.capacity, newHistory.toJS());
  }

  getHistory(): T[] {
    return this.history.toJS();
  }

  hasItem(item: T): boolean {
    return this.history.has(item);
  }

  clearHistory(): RecentHistoryManager<T> {
    return new RecentHistoryManager(this.capacity); // Re-initialize with empty history
  }
}

// Example Usage:
const manager = new RecentHistoryManager<string>(3);
console.log('Initial History:', manager.getHistory()); // []

const m1 = manager.viewItem('Product A');
console.log('After A:', m1.getHistory()); // ['Product A']

const m2 = m1.viewItem('Product B');
console.log('After B:', m2.getHistory()); // ['Product A', 'Product B']

const m3 = m2.viewItem('Product C');
console.log('After C:', m3.getHistory()); // ['Product A', 'Product B', 'Product C']

const m4 = m3.viewItem('Product D'); // Capacity reached, A should be removed
console.log('After D (A removed):', m4.getHistory()); // ['Product B', 'Product C', 'Product D']

const m5 = m4.viewItem('Product B'); // B viewed again, should move to end
console.log('After B again:', m5.getHistory()); // ['Product C', 'Product D', 'Product B']

console.log('Has Product C?', m5.hasItem('Product C')); // true
console.log('Has Product A?', m5.hasItem('Product A')); // false

const m6 = m5.clearHistory();
console.log('After Clear:', m6.getHistory()); // []

console.log('Original manager history (unchanged):', manager.getHistory()); // []

// More edge cases
const managerWithInitial = new RecentHistoryManager<number>(2, [10, 20, 10, 30]); // Unique and slice to capacity
console.log('Manager with initial values:', managerWithInitial.getHistory()); // [10, 20] (order of initial elements retained, then truncated)

const m7 = managerWithInitial.viewItem(40);
console.log('After 40:', m7.getHistory()); // [20, 40] (10 removed)

const m8 = m7.viewItem(20); // 20 viewed again
console.log('After 20 again:', m8.getHistory()); // [40, 20]

const managerEmptyCapacity = new RecentHistoryManager<string>(0);
console.log('Empty capacity manager:', managerEmptyCapacity.getHistory()); // []
const m9 = managerEmptyCapacity.viewItem('X');
console.log('Add to empty capacity:', m9.getHistory()); // [] (item added then immediately removed)

