```typescript
import { Map, List } from 'immutable';

function phoneReducer(state = Map(), action) {
  switch (action.type) {
    case 'ADD_PHONE':
      // Ensure 'phones' key exists and is a List, then push the new phone
      // List() is provided as a notSetValue to update, so it initializes 'phones' as an empty List if it doesn't exist
      return state.update('phones', List(), phones => phones.push(action.phone));
    case 'REMOVE_PHONE':
      // Filter out the phone to be removed, returning a new List
      return state.update('phones', List(), phones => phones.filterNot(phone => phone === action.phone));
    default:
      return state;
  }
}

