import { Map, List, fromJS } from 'immutable';

// 1. Initial Cart State
const initialCartState = Map({
  items: List(),
  totalPrice: 0,
});

// Helper to calculate total price
const calculateTotalPrice = (items) => {
  return items.reduce((sum, item) => sum + (item.get('price') * item.get('quantity')), 0);
};

/**
 * Adds an item to the cart or updates its quantity if already present.
 * @param {Immutable.Map} cart The current immutable cart state.
 * @param {object} item The plain JS item to add/update: { id, name, price, quantity }
 * @returns {Immutable.Map} A new immutable cart state.
 */
function addItemToCart(cart, item) {
  const existingItemIndex = cart.get('items').findIndex(cartItem => cartItem.get('id') === item.id);

  let newItems;
  if (existingItemIndex !== -1) {
    // Item exists, update quantity
    newItems = cart.get('items').update(existingItemIndex, cartItem =>
      cartItem.update('quantity', q => q + item.quantity)
    );
  } else {
    // Item does not exist, add new item (convert to Immutable Map first)
    newItems = cart.get('items').push(fromJS(item));
  }

  return cart.set('items', newItems)
             .set('totalPrice', calculateTotalPrice(newItems));
}

/**
 * Removes an item from the cart.
 * @param {Immutable.Map} cart The current immutable cart state.
 * @param {string} itemId The ID of the item to remove.
 * @returns {Immutable.Map} A new immutable cart state.
 */
function removeItemFromCart(cart, itemId) {
  const newItems = cart.get('items').filterNot(item => item.get('id') === itemId);

  // If the list size hasn't changed, the item wasn't found, so return original cart
  if (newItems.size === cart.get('items').size && newItems !== cart.get('items')) {
      return cart; // No item removed, no state change
  }

  return cart.set('items', newItems)
             .set('totalPrice', calculateTotalPrice(newItems));
}

/**
 * Updates the quantity of an item in the cart.
 * @param {Immutable.Map} cart The current immutable cart state.
 * @param {string} itemId The ID of the item to update.
 * @param {number} newQuantity The new quantity for the item.
 * @returns {Immutable.Map} A new immutable cart state.
 */
function updateItemQuantity(cart, itemId, newQuantity) {
  if (newQuantity <= 0) {
    return removeItemFromCart(cart, itemId); // Remove if quantity is 0 or less
  }

  const existingItemIndex = cart.get('items').findIndex(cartItem => cartItem.get('id') === itemId);

  if (existingItemIndex === -1) {
    return cart; // Item not found, return original cart
  }

  const newItems = cart.get('items').update(existingItemIndex, item =>
    item.set('quantity', newQuantity)
  );

  return cart.set('items', newItems)
             .set('totalPrice', calculateTotalPrice(newItems));
}

// --- Test Cases ---
let currentCart = initialCartState;
console.log('Initial Cart:', currentCart.toJS()); // { items: [], totalPrice: 0 }

// Test 1: Add first item
currentCart = addItemToCart(currentCart, { id: 'p1', name: 'Laptop', price: 1200, quantity: 1 });
console.log('Test 1 (Add Laptop):', currentCart.toJS()); // { items: [{ id: 'p1', name: 'Laptop', price: 1200, quantity: 1 }], totalPrice: 1200 }

// Test 2: Add second item
currentCart = addItemToCart(currentCart, { id: 'p2', name: 'Mouse', price: 25, quantity: 2 });
console.log('Test 2 (Add Mouse):', currentCart.toJS()); // { items: [..., { id: 'p2', name: 'Mouse', price: 25, quantity: 2 }], totalPrice: 1250 }

// Test 3: Add existing item (Laptop) - quantity should increase
const originalCartBeforeDup = currentCart;
currentCart = addItemToCart(currentCart, { id: 'p1', name: 'Laptop', price: 1200, quantity: 1 });
console.log('Test 3 (Add Laptop again):', currentCart.toJS()); // { items: [{ id: 'p1', ..., quantity: 2 }, ...], totalPrice: 2450 }
console.log('Original cart unchanged:', originalCartBeforeDup.toJS() === currentCart.toJS()); // Should be false

// Test 4: Update quantity of an item
currentCart = updateItemQuantity(currentCart, 'p2', 5);
console.log('Test 4 (Update Mouse quantity to 5):', currentCart.toJS()); // { items: [..., { id: 'p2', ..., quantity: 5 }], totalPrice: 2525 }

// Test 5: Remove an item
currentCart = removeItemFromCart(currentCart, 'p1');
console.log('Test 5 (Remove Laptop):', currentCart.toJS()); // { items: [{ id: 'p2', ..., quantity: 5 }], totalPrice: 125 }

// Test 6: Try to remove non-existent item
const cartBeforeNonExistentRemoval = currentCart;
currentCart = removeItemFromCart(currentCart, 'p99');
console.log('Test 6 (Remove non-existent):', currentCart.toJS()); // Should be same as previous state
console.log('Cart unchanged after non-existent removal:', cartBeforeNonExistentRemoval === currentCart); // Should be true

// Test 7: Update quantity to 0 (should remove item)
currentCart = updateItemQuantity(currentCart, 'p2', 0);
console.log('Test 7 (Update Mouse quantity to 0):', currentCart.toJS()); // { items: [], totalPrice: 0 }

// Test 8: Empty cart state
currentCart = initialCartState;
console.log('Test 8 (Reset to initial state):', currentCart.toJS()); // { items: [], totalPrice: 0 }

