import { Map, fromJS } from 'immutable';

const initialSettingsState = Map({
  theme: 'light',
  notificationsEnabled: true,
  language: 'en'
});

function settingsReducer(state = initialSettingsState, action) {
  switch (action.type) {
    case 'SET_THEME':
      return state.set('theme', action.payload.newTheme);
    case 'TOGGLE_NOTIFICATIONS':
      return state.update('notificationsEnabled', enabled => !enabled);
    case 'CHANGE_LANGUAGE':
      return state.set('language', action.payload.newLang);
    case 'RESET_SETTINGS':
      // Return a *new* instance of the initial state, not the same reference
      return initialSettingsState;
    case 'UPDATE_MULTIPLE_SETTINGS':
      // Merge assumes action.payload can be directly merged. 
      // If it's a plain JS object, fromJS will convert it to Immutable for merging.
      return state.merge(action.payload);
    default:
      return state;
  }
}

// --- Test Cases ---
let currentSettings = settingsReducer(undefined, { type: '@@INIT' }); // Simulate Redux init
console.log('Initial settings:', currentSettings.toJS());
// Expected: { theme: 'light', notificationsEnabled: true, language: 'en' }

const state1 = settingsReducer(currentSettings, { type: 'SET_THEME', payload: { newTheme: 'dark' } });
console.log('After SET_THEME to dark:', state1.toJS());
// Expected: { theme: 'dark', notificationsEnabled: true, language: 'en' }
