import { Map, List } from 'immutable';
import { createSelector } from 'reselect';

const blogAppState = Map({
  posts: List([
    Map({ id: 'post1', title: 'Intro to Redux', authorId: 'userA', tags: List(['redux', 'js']), published: true, views: 150 }),
    Map({ id: 'post2', title: 'Understanding Immutable.js', authorId: 'userB', tags: List(['immutablejs', 'js']), published: true, views: 200 }),
    Map({ id: 'post3', title: 'Advanced React Hooks', authorId: 'userA', tags: List(['react', 'hooks']), published: false, views: 50 }),
    Map({ id: 'post4', title: 'Node.js Performance', authorId: 'userC', tags: List(['nodejs', 'backend']), published: true, views: 100 })
  ]),
  users: Map({
    'userA': Map({ id: 'userA', name: 'Alice', role: 'admin' }),
    'userB': Map({ id: 'userB', name: 'Bob', role: 'editor' }),
    'userC': Map({ id: 'userC', name: 'Charlie', role: 'viewer' })
  }),
  filters: Map({
    showPublishedOnly: true,
    searchQuery: ''
  })
});

// 1. getAllPosts
const getAllPosts = state => state.get('posts');

// 2. getUsers
const getUsers = state => state.get('users');

// 3. getPublishedPosts (using createSelector for memoization)
const getPublishedPosts = createSelector(
  [getAllPosts],
  (posts) => posts.filter(post => post.get('published'))
);

// 4. getPostsByAuthor (takes an additional argument)
const getPostsByAuthor = createSelector(
  [getAllPosts, (state, authorId) => authorId], // Second input selector gets the argument
  (posts, authorId) => posts.filter(post => post.get('authorId') === authorId)
);

// 5. getPostWithAuthorName
const getPostWithAuthorName = createSelector(
  [getAllPosts, getUsers],
  (posts, users) => {
    return posts.map(post => {
      const author = users.get(post.get('authorId'));
      return post.set('authorName', author ? author.get('name') : 'Unknown');
    });
  }
);

