const express = require('express');
const cookieParser = require('cookie-parser');
const app = express();
const PORT = 3000;

app.use(express.json()); // To parse JSON request bodies
app.use(cookieParser()); // To parse incoming cookies

// Mock login endpoint
app.post('/login', (req, res) => {
  // In a real app, you'd validate credentials here
  const mockAuthToken = 'mock-secure-jwt-12345';

  // Set the authToken cookie with secure flags
  res.cookie('authToken', mockAuthToken, {
    httpOnly: true, // Prevents client-side JavaScript access
    secure: true,   // Only send over HTTPS (important for production)
    sameSite: 'Lax', // Protects against most CSRF attacks
    maxAge: 3600000 // Cookie expires in 1 hour (in milliseconds)
  });

  res.status(200).json({ message: 'Login successful, authentication cookie set!' });
});

// Dashboard endpoint requiring authentication
app.get('/dashboard', (req, res) => {
  if (req.cookies.authToken) {
    res.status(200).send('Welcome to the protected dashboard! Your auth token is present (server-side).');
  } else {
    res.status(401).send('Unauthorized: No authentication token found.');
  }
});

// Client-side access test (should NOT show HttpOnly cookie)
app.get('/client-test', (req, res) => {
  res.send(`
    <!DOCTYPE html>
    <html>
    <body>
        <h1>Client-Side Cookie Access Test</h1>
        <p>Check console for document.cookie:</p>
        <script>
            console.log('document.cookie:', document.cookie);
            // This should NOT show the 'authToken' if HttpOnly is true
            document.body.innerHTML += '<p>document.cookie result in console.</p>';
        </script>
    </body>
    </html>
  `);
});

app.listen(PORT, () => {
  console.log(`Server running on http://localhost:${PORT}`);
  console.log('Try: POST http://localhost:3000/login (e.g., with empty JSON body)');
  console.log('Then: GET http://localhost:3000/dashboard');
  console.log('Then: GET http://localhost:3000/client-test');
});

