const PORT = 4000;

app.use(express.json()); // For parsing application/json

// CORS middleware
app.use((req, res, next) => {
  // 1. Allow requests from http://localhost:3000
  res.header('Access-Control-Allow-Origin', 'http://localhost:3000');

  // 2. Allow specific methods (GET, POST, and OPTIONS for preflight)
  res.header('Access-Control-Allow-Methods', 'GET, POST, OPTIONS');

  // 3. Allow specific headers (Content-Type, Authorization, X-Custom-Header)
  res.header('Access-Control-Allow-Headers', 'Content-Type, Authorization, X-Custom-Header');

  // 4. Handle preflight OPTIONS requests
  // When the browser sends an OPTIONS request for a non-simple request, 
  // it only needs the headers, no actual content.
  if (req.method === 'OPTIONS') {
    return res.sendStatus(204); // Send 204 No Content status for successful preflight
  }

  next();
});

