document.addEventListener('DOMContentLoaded', () => {
  const taskInput = document.getElementById('taskInput');
  const addTaskBtn = document.getElementById('addTaskBtn');
  const todoList = document.getElementById('todoList');

  // Add Task functionality
  addTaskBtn.addEventListener('click', () => {
    const taskText = taskInput.value.trim();
    if (taskText !== '') {
      const listItem = document.createElement('li');
      // Use a span for the text content to make it a distinct target for toggling
      listItem.innerHTML = `
        <span>${taskText}</span>
        <button class="delete-btn">X</button>
      `;
      todoList.appendChild(listItem);
      taskInput.value = ''; // Clear input
    }
  });

  // Event Delegation for Marking as Completed and Deleting Tasks
  todoList.addEventListener('click', (event) => {
    const target = event.target; // The actual element that was clicked

    // 1. Delete Task
    if (target.classList.contains('delete-btn')) {
      const listItemToDelete = target.closest('li'); // Find the nearest parent <li>
      if (listItemToDelete) {
        todoList.removeChild(listItemToDelete);
        console.log('Task deleted:', listItemToDelete.querySelector('span').textContent);
      }
    } 
    // 2. Mark as Completed (toggle 'completed' class on the li item)
    // Check if the clicked element is the span (task text) or the li itself
    else if (target.tagName === 'SPAN' && target.parentElement.tagName === 'LI') {
      target.parentElement.classList.toggle('completed');
      console.log('Task status toggled for:', target.textContent);
    } else if (target.tagName === 'LI') {
      // If clicking directly on the LI (and not the span), also toggle
      target.classList.toggle('completed');
      console.log('Task status toggled for:', target.querySelector('span').textContent);
    }
  });
});

