document.addEventListener('DOMContentLoaded', () => {
  const fileSystem = document.getElementById('fileSystem');

  // Function to remove 'highlighted' class from all elements
  function clearAllHighlights() {
    document.querySelectorAll('.highlighted').forEach(el => el.classList.remove('highlighted'));
  }

  fileSystem.addEventListener('click', (event) => {
    const clickedItem = event.target.closest('li'); // Find the clicked <li> or its ancestor

    // Prevent default actions for links if any, and stop propagation if needed
    event.preventDefault(); // Prevents default link behavior if LIs contained <a>
    // event.stopPropagation(); // Not strictly needed here, as we handle everything on fileSystem

    if (clickedItem && fileSystem.contains(clickedItem)) { // Ensure the click is within an LI of our tree
      clearAllHighlights(); // Start fresh before applying new highlights

      // 1. Highlight clicked item and its ancestors
      let current = clickedItem;
      while (current && current !== fileSystem) {
        current.classList.add('highlighted');
        // Move up to the next parent <li> that is not the root fileSystem ul
        // parentElement.closest('li') is robust for skipping intermediate ul/text nodes
        current = current.parentElement.closest('li');
      }

      // 2. De-highlight siblings and their descendants
      let node = clickedItem.parentNode.firstElementChild; // Start from the first child of the parent UL
      while (node) {
        if (node.tagName === 'LI' && node !== clickedItem) {
          node.classList.remove('highlighted'); // Remove highlight from sibling LI

          // Remove highlight from all descendants of this sibling
          node.querySelectorAll('li.highlighted').forEach(descendant => {
            descendant.classList.remove('highlighted');
          });
        }
        node = node.nextElementSibling; // Move to the next sibling element
      }

    } else {
      // Clicking outside any <li> (but within fileSystem, or a general click)
      // This handles the reset requirement.
      clearAllHighlights();
    }
  });
});

