tasks.solutionCode:
document.addEventListener('DOMContentLoaded', () => {
  const searchInput = document.getElementById('searchInput');
  const loadingIndicator = document.getElementById('loadingIndicator');
  const resultsContainer = document.getElementById('resultsContainer');

  let debounceTimer;      // Timer for debouncing
  let currentController;  // Stores the AbortController for the currently active fetch request

  function showLoading() {
    loadingIndicator.style.display = 'block';
    resultsContainer.innerHTML = ''; // Clear previous results immediately
  }

  function hideLoading() {
    loadingIndicator.style.display = 'none';
  }

  async function fetchUsers(query) {
    // If there's an ongoing request, abort it before starting a new one
    if (currentController) {
      currentController.abort();
      console.log('Previous request aborted.');
    }

    showLoading();

    // Create a new AbortController for the current request
    currentController = new AbortController();
    const signal = currentController.signal;

    const url = 'https://jsonplaceholder.typicode.com/users';

    try {
      const response = await fetch(url, { signal });

      if (!response.ok) {
        // Read response text for more detailed error, if available
        const errorBody = await response.text();
        throw new Error(`HTTP error! status: ${response.status} - ${errorBody || response.statusText}`);
      }

      const users = await response.json();
      const filteredUsers = users.filter(user => 
        user.name.toLowerCase().includes(query.toLowerCase())
      );

      displayResults(filteredUsers);

    } catch (error) {
      // Check if the error is due to an intentional abort
      if (error.name === 'AbortError') {
        console.log('Fetch request was cancelled by AbortController.');
        // Do not display an error message to the user for cancellation
        if (query.length === 0) { // If query is empty after abort, clear results
          resultsContainer.innerHTML = '';
        } else { // Otherwise, it means a newer request is coming or is active, so don't show 'No users found' just yet
          // You might want to keep the loading indicator if a new fetch is about to start
        }
      } else {
        // Display general error message for other types of errors (network, parsing, etc.)
        displayError(error.message);
        console.error('Fetch error:', error);
      }
    } finally {
      // Hide loading indicator once the request (or abort) is handled
      hideLoading();
      // Clear the currentController reference if this fetch has completed (successfully or with error)
      // This is important so the next fetch doesn't try to abort an already finished/cleared controller.
      if (currentController && !currentController.signal.aborted) { // Only clear if it wasn't aborted by a newer request
          currentController = null;
      }
    }
  }

  function displayResults(users) {
    resultsContainer.innerHTML = ''; // Clear previous results
    if (users.length === 0) {
      resultsContainer.innerHTML = '<p>No users found.</p>';
    } else {
      const ul = document.createElement('ul');
      ul.style.listStyleType = 'none';
      ul.style.paddingLeft = '0';
      users.forEach(user => {
        const li = document.createElement('li');
        li.textContent = user.name;
        li.style.padding = '5px 0';
        li.style.borderBottom = '1px solid #eee';
        ul.appendChild(li);
      });
      resultsContainer.appendChild(ul);
    }
  }

  function displayError(message) {
    resultsContainer.innerHTML = `<p style="color: red;">Error: ${message}</p>`;
  }

  // Implement debouncing on searchInput 'input' event
  searchInput.addEventListener('input', (event) => {
    const query = event.target.value.trim();

    clearTimeout(debounceTimer);

    if (query.length > 0) {
      showLoading(); // Show loading immediately on input change
      debounceTimer = setTimeout(() => {
        fetchUsers(query);
      }, 300); // Debounce delay: 300ms
    } else {
      // If query is empty, clear results and hide loading/errors
      resultsContainer.innerHTML = '';
      hideLoading();
      // Abort any pending requests if user clears search field quickly
      if (currentController) {
        currentController.abort();
        currentController = null;
      }
    }
  });
});

