    worker.terminate(); // Terminate existing worker if any
  }
  worker = new Worker('heavy_calc_worker.js');
  statusParagraph.textContent = 'Status: Calculation started...';
  calculateBtn.disabled = true;

  // Send data to the worker
  const dataToSend = { iterations: 100000000 };
  worker.postMessage(dataToSend);

  worker.onmessage = (event) => {
    console.log('Main thread received:', event.data);
    statusParagraph.textContent = 'Status: ' + event.data.message + ' Result: ' + event.data.result;
    calculateBtn.disabled = false;
    worker.terminate(); // Terminate worker after receiving result
    worker = null;
  };

  worker.onerror = (error) => {
    console.error('Worker error in main thread:', error);
    statusParagraph.textContent = 'Status: Error during calculation.';
    calculateBtn.disabled = false;
    if (worker) worker.terminate();
