ctx.lineTo(200, 100); // Line to this point
ctx.lineTo(150, 200);
ctx.closePath(); // Connect back to start
ctx.strokeStyle = 'red';
ctx.lineWidth = 3;
ctx.stroke(); // Draw the outline
ctx.fillStyle = 'lightgreen';
ctx.fill(); // Fill the shape

// Drawing a filled black circle
ctx.beginPath();
ctx.arc(250, 150, 50, 0, Math.PI * 2); // x, y, radius, start angle, end angle
ctx.fillStyle = 'black';
ctx.fill();

// Drawing text
